import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import NumericInput from "react-numeric-input";
import { FcPlus } from "react-icons/fc";
import { FaMinusCircle } from "react-icons/fa";
import { MobileView } from "react-device-detect";
const EditableTable = ({ index, thead, SetData, style }) => {
  const [isDesc, SetDesc] = useState({
    set: [1],
    reps: [0],
    weight: [5],
  });
  Number.prototype[Symbol.iterator] = function* (i = 1) {
    while (i < this) {
      yield i++;
    }
  };
  const [isSetDropdown, SetDropdownList] = useState([...100]);
  const [isRepsDropdown, RepsDropdownList] = useState([...200]);
  const [isWeightsDropdown, WeightList] = useState([...2000]);

  useEffect(() => {
    SetData(
      {
        set: isDesc.set,
        reps: isDesc.reps,
        weight: isDesc.weight,
      },
      index
    );
  }, [isDesc]);
  let theadArr = [];
  for (let key in thead) {
    if (thead.hasOwnProperty(key)) {
      theadArr.push(<th key={key}>{thead[key]}</th>);
    } else {
      theadArr.push(<th key={key}>{key}</th>);
    }
  }

  const handleDelete = (e, idx) => {
    e.preventDefault();
    if (isDesc.set.length > 1) {
      let newIndexArr = [];
      isDesc.set.forEach((el, index) => {
        if (index > idx) {
          newIndexArr.push(el - 1);
        }
      });
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...newIndexArr],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    } else {
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...isDesc.set.slice(idx + 1)],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    }
  };
  return (
    <div>
      <Table
        responsive
        striped
        borderless
        variant={"dark"}
        style={style !== undefined ? style : { color: "white" }}
        onChange={() => {
          SetData(
            {
              set: isDesc.set,
              reps: isDesc.reps,
              weight: isDesc.weight,
            },
            index
          );
        }}
      >
        <thead className="thead-dark">
          <tr id="table-thead-tr">
            <th />
            {theadArr}
          </tr>
        </thead>
        <tbody>
          {isDesc.set.map((el, idx) => {
            return (
              <tr key={"set-row" + idx}>
                <td>
                  <button
                    style={{
                      borderRadius: "10vw",
                      background: "none",
                      borderStyle: "hidden",
                    }}
                    onClick={(e) => handleDelete(e, idx)}
                  >
                    <FaMinusCircle size={"1em"} style={{ color: "red" }} />
                  </button>
                </td>
                <>
                  <td>
                    <NumericInput
                      mobile={"auto"}
                      size={5}
                      key={"set-content" + idx}
                      id={"set-" + idx}
                      min={1}
                      max={100}
                      style={{
                        input: {
                          color: "blue",
                        },
                      }}
                      placeholder={isDesc.set[idx]}
                      onChange={(e) => {
                        console.log(e);
                        SetDesc({
                          set: [
                            ...isDesc.set.slice(0, idx),
                            e,
                            ...isDesc.set.slice(idx + 1),
                          ],
                          reps: isDesc.reps,
                          weight: isDesc.weight,
                        });
                      }}
                      list={"set"}
                    />
                    <MobileView>
                      <datalist id="set">
                        {isSetDropdown.map((el) => {
                          return <option value={el}></option>;
                        })}
                      </datalist>
                    </MobileView>
                  </td>
                </>
                <>
                  <td>
                    <NumericInput
                      mobile={"auto"}
                      size={5}
                      key={"workout-desc" + idx}
                      step={5.0}
                      min={0}
                      precision={1}
                      max={1000}
                      style={{
                        input: {
                          color: "blue",
                        },
                      }}
                      placeholder={isDesc.reps[idx]}
                      onChange={(e) => {
                        SetDesc({
                          set: isDesc.set,
                          reps: [
                            ...isDesc.reps.slice(0, idx),
                            e,
                            ...isDesc.reps.slice(idx + 1),
                          ],
                          weight: isDesc.weight,
                        });
                      }}
                      list={"reps"}
                    />
                    <MobileView>
                      <datalist id="reps">
                        {isRepsDropdown.map((el) => {
                          return <option value={el}></option>;
                        })}
                      </datalist>
                    </MobileView>
                  </td>
                </>
                <>
                  <td>
                    <NumericInput
                      mobile={"auto"}
                      size={5}
                      key={"workout-desc" + idx}
                      step={5.0}
                      min={0}
                      precision={1}
                      max={2000}
                      style={{
                        input: {
                          color: "green",
                          type: "numeric",
                          class: "spinner",
                        },
                      }}
                      placeholder={isDesc.weight[idx]}
                      onChange={(e) => {
                        SetDesc({
                          set: isDesc.set,
                          reps: isDesc.reps,
                          weight: [
                            ...isDesc.weight.slice(0, idx),
                            e,
                            ...isDesc.weight.slice(idx + 1),
                          ],
                        });
                      }}
                      list={"weight"}
                    />
                    <MobileView>
                      <datalist id="weight">
                        {isWeightsDropdown.map((el) => {
                          return <option value={el}></option>;
                        })}
                      </datalist>
                    </MobileView>
                  </td>
                </>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <div style={{ textAlign: "center" }}>
        <button
          style={{
            margin: "auto",
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            if (isDesc.set.length > 0) {
              SetDesc({
                set: [...isDesc.set, isDesc.set.slice(-1)[0] + 1],
                reps: [...isDesc.reps, isDesc.reps.slice(-1)[0]],
                weight: [...isDesc.weight, isDesc.weight.slice(-1)[0]],
              });
            } else {
              SetDesc({
                set: [...isDesc.set, 1],
                reps: [...isDesc.reps, 0],
                weight: [...isDesc.weight, 5],
              });
            }
          }}
        >
          <FcPlus size={"2em"} />
        </button>
      </div>
    </div>
  );
};
export default EditableTable;
