import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import ToggleEdit from "../../partials/ToggleEdit";
import SubmitUpdate from "../../partials/SubmitUpdate";
import EditWorkout from "./actions/edit/EditWorkout";
import RemoveWorkout from "./actions/remove/RemoveWorkout";
import EditableTable from "../../partials/EditableTable";
import update from "immutability-helper";

const ViewWorkoutList = ({ el, isCurrID, updateData, logIndex, user }) => {
  const [isEdit, SetEdit] = useState([]);
  const [isDelete, SetDelete] = useState([]);
  const [isEditList, SetEditList] = useState(el);
  useEffect(() => {
    for (let i = 0; i < el[0][1].length; i++) {
      SetEdit((isEdit) => [...isEdit, false]);
      SetDelete((isDelete) => [...isDelete, false]);
    }
  }, []);
  const SetData = (state, idx) => {
    SetEditList((isEditList) =>
      update(isEditList, {
        0: {
          1: {
            [idx]: {
              set: { $set: state.set },
              reps: { $set: state.reps },
              weight: { $set: state.weight },
            },
          },
        },
      })
    );
  };
  return el.map((log) => {
    if (log[0] === "exercises") {
      return (
        <>
          {log[1].map((ex, index) => {
            return (
              <div>
                {!isEdit[index] ? (
                  <>
                    <h3>{ex.name}</h3>
                    <p>{ex.description}</p>
                    <Table striped borderless variant={"dark"}>
                      <thead className="thead-dark">
                        <tr id="table-thead-tr">
                          <th />
                          <th>Set</th>
                          <th>Reps</th>
                          <th>Weight</th>
                        </tr>
                      </thead>
                      <tbody>
                        {ex.set.map((set, idx) => {
                          return (
                            <tr>
                              <td></td>
                              <td>{set}</td>
                              <td>{ex.reps[idx]}</td>
                              <td>{ex.weight[idx]}</td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </Table>
                  </>
                ) : (
                  <>
                    <EditWorkout
                      SetEditList={SetEditList}
                      isEditList={isEditList}
                      li={index}
                      ex={ex}
                    />
                    <EditableTable
                      index={index}
                      thead={["Set", "Reps", "Weight"]}
                      SetData={SetData}
                      style={{
                        color: "black",
                        width: "inherit",
                        fontSize: "1.5vh",
                        margin: "0",
                      }}
                      dataAvailable={[ex.set, ex.reps, ex.weight]}
                    />
                    <hr />
                    <SubmitUpdate
                      index={logIndex}
                      workoutIndex={index}
                      isCurrID={isCurrID}
                      isEdit={isEdit}
                      SetEdit={SetEdit}
                      isEditList={isEditList}
                      updateData={updateData}
                    />
                  </>
                )}

                <div
                  className={"d-flex d-inline-flex"}
                  style={{ fontSize: "1.5vh" }}
                >
                  <RemoveWorkout
                    exercise={ex}
                    logIndex={logIndex}
                    isCurrID={isCurrID}
                    SetEditList={SetEditList}
                    isEditList={isEditList}
                    updateData={updateData}
                    textColor={{ color: "black" }}
                    user={user}
                  />
                  <ToggleEdit
                    isEdit={isEdit}
                    SetEdit={SetEdit}
                    index={index}
                    target={"Exercise"}
                    multiple={true}
                  />
                </div>
                <hr />
              </div>
            );
          })}
        </>
      );
    }
  });
};
export default ViewWorkoutList;
