import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Accordion, Card, Table } from "react-bootstrap";
import { FcPlus } from "react-icons/fc";
import { useAuth0 } from "@auth0/auth0-react";
import EditableTable from "../partials/EditableTable";
const ViewLogList = ({ auth }) => {
  const { user } = useAuth0();
  const [isShown, SetShown] = useState([]);
  const [isDisplayList, SetDisplayList] = useState([]);
  const [isNewExercise, SetNewExercise] = useState([]);
  const [isCurrID, SetCurrID] = useState([]);
  const [isNewLog, SetNewLog] = useState(null);
  const [isDesc, SetDesc] = useState({
    set: [1],
    reps: [1],
    weight: [5],
  });

  useEffect(() => {
    if (auth) {
      axios.get(`api/logs`).then((res) => {
        res.data.forEach((el, idx) => {
          if (el.username === user.email) {
            let currList = [];
            for (let attr in el) {
              if (el.hasOwnProperty(attr)) {
                if (attr === "_id")
                  SetCurrID((isCurrID) => [...isCurrID, el[attr]]);
                currList.push({ key: attr, val: el[attr] });
              }
            }
            SetShown((isShown) => [...isShown, false]);
            SetDisplayList((isDisplayList) => [...isDisplayList, currList]);
          }
        });
      });
    }
  }, []);

  const SetExerciseDescription = (state) => {
    SetDesc({
      set: state.set,
      reps: state.reps,
      weight: state.weight,
    });
  };
  const handleDelete = (e, idx) => {
    e.preventDefault();
    if (isDesc.set.length > 1) {
      let newIndexArr = [];
      isDesc.set.forEach((el, index) => {
        if (index > idx) {
          newIndexArr.push(el - 1);
        }
      });
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...newIndexArr],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    } else {
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...isDesc.set.slice(idx + 1)],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    }
  };
  return (
    <>
      <div>
        {isDisplayList.map((el, index) => {
          return (
            <Accordion
              key={"log-disp" + index}
              className={"p-2"}
              defaultActiveKey="0"
            >
              <Card>
                {
                  <>
                    {Object.entries(el).map((log) => {
                      if (log[1].key === "title") {
                        return (
                          <Accordion.Toggle
                            as={Card.Header}
                            eventKey="0"
                            id={"log-title-" + `${index}`}
                          >
                            {log[1].val}
                          </Accordion.Toggle>
                        );
                      } else if (log[1].key === "description") {
                        return (
                          <Accordion.Collapse eventKey="0">
                            <Card.Body>
                              <div className={"p-2"}>
                                <p id={"log-desc-" + `${index}`}>
                                  {log[1].key === "description" && log[1].val}
                                </p>
                              </div>
                              <>
                                {Object.entries(el).map((log) => {
                                  if (log[1].key === "exercises") {
                                    return (
                                      <>
                                        {log[1].val.map((ex) => {
                                          return (
                                            <div
                                              className={"container"}
                                              style={{ overflowX: "auto" }}
                                            >
                                              <h3>{ex.name}</h3>
                                              <Table
                                                striped
                                                borderless
                                                variant={"dark"}
                                              >
                                                <thead className="thead-dark">
                                                  <tr id="table-thead-tr">
                                                    <th />
                                                    <th>Set</th>
                                                    <th>Reps</th>
                                                    <th>Weight</th>
                                                  </tr>
                                                </thead>
                                                <tbody>
                                                  {ex.set.map((set, idx) => {
                                                    return (
                                                      <tr>
                                                        <td></td>
                                                        <td>{set}</td>
                                                        <td>{ex.reps[idx]}</td>
                                                        <td>
                                                          {ex.weight[idx]}
                                                        </td>
                                                      </tr>
                                                    );
                                                  })}
                                                </tbody>
                                              </Table>
                                            </div>
                                          );
                                        })}
                                      </>
                                    );
                                  }
                                })}
                              </>
                              {isShown[index] && (
                                <>
                                  <div className={"p-2 d-flex d-inline-flex"}>
                                    {isNewExercise[index].title}
                                  </div>
                                  <>{isNewExercise[index].content}</>
                                </>
                              )}
                              <div
                                className={"d-flex d-inline-flex"}
                                style={{ textAlign: "center" }}
                              >
                                <button
                                  id={index}
                                  style={{
                                    borderRadius: "10vw",
                                    background: "none",
                                    borderStyle: "hidden",
                                  }}
                                  onClick={(e) => {
                                    SetShown((isShown) => [
                                      ...isShown.slice(0, index),
                                      true,
                                      ...isShown.slice(index + 1),
                                    ]);
                                    e.preventDefault();
                                    SetNewExercise((isNewExercise) => [
                                      ...isNewExercise,
                                      {
                                        title: (
                                          <input
                                            size={5}
                                            id={"new-workout-name"}
                                          />
                                        ),
                                        content: (
                                          <div>
                                            <EditableTable
                                              thead={["Set", "Reps", "Weight"]}
                                              SetData={SetExerciseDescription}
                                              style={{ color: "black" }}
                                            />
                                            <div
                                              className={"d-flex d-inline-flex"}
                                              style={{ float: "right" }}
                                            >
                                              <Button
                                                id={index}
                                                className={"p-2"}
                                                variant={"outline-primary"}
                                                onClick={(e, li) => {
                                                  e.preventDefault();
                                                  if (
                                                    document.querySelector(
                                                      "#new-workout-name"
                                                    ).value === null ||
                                                    document.querySelector(
                                                      "#new-workout-name"
                                                    ).value === ""
                                                  ) {
                                                    alert(
                                                      "Please provide the name"
                                                    );
                                                    return;
                                                  }
                                                  let date = new Date();
                                                  axios
                                                    .post(
                                                      `api/logs/add/${
                                                        isCurrID[e.target.id]
                                                      }`,
                                                      {
                                                        username: user.email,
                                                        title: document.getElementById(
                                                          "log-title-" +
                                                            `${e.target.id}`
                                                        ).innerText,
                                                        name: document.querySelector(
                                                          "#new-workout-name"
                                                        ).value,
                                                        description: document.getElementById(
                                                          "log-desc-" +
                                                            `${e.target.id}`
                                                        ).innerText,
                                                        set: isDesc.set,
                                                        reps: isDesc.reps,
                                                        weight: isDesc.weight,
                                                        date: date,
                                                      }
                                                    )
                                                    .then((response) => {
                                                      console.log(response);
                                                      // window.location.reload();
                                                    })
                                                    .catch((err) => {
                                                      console.log(err);
                                                    });
                                                }}
                                              >
                                                Submit
                                              </Button>
                                              <Button
                                                className={"ml-2"}
                                                variant={"outline-danger"}
                                                onClick={(e) => {
                                                  e.preventDefault();
                                                  SetNewExercise(
                                                    (isNewExercise) => [
                                                      ...isNewExercise.slice(
                                                        0,
                                                        index
                                                      ),
                                                      {
                                                        title: null,
                                                        content: null,
                                                      },
                                                      ...isNewExercise.slice(
                                                        index + 1
                                                      ),
                                                    ]
                                                  );
                                                }}
                                              >
                                                Cancel
                                              </Button>
                                            </div>
                                          </div>
                                        ),
                                      },
                                    ]);
                                  }}
                                >
                                  <FcPlus size={"2em"} />
                                </button>
                                <br />

                                <span
                                  className="p-2 font-weight-bold"
                                  style={{ color: "black" }}
                                >
                                  Add New Exercise
                                </span>
                              </div>
                            </Card.Body>
                          </Accordion.Collapse>
                        );
                      }
                    })}
                  </>
                }
              </Card>

              <Card>
                {Object.entries(el).map((log) => {
                  if (log[1].key === "date") {
                    return log[1].val;
                  }
                })}
              </Card>
            </Accordion>
          );
        })}
      </div>
      {isNewLog}
      <button
        style={{
          borderRadius: "10vw",
          background: "none",
          borderStyle: "hidden",
        }}
        onClick={(e) => {
          e.preventDefault();
          SetNewLog(
            <>
              <Accordion className={"p-2"} defaultActiveKey="0">
                <Card>
                  <Card.Header>
                    Title: <input id={"new-log-title"} />
                  </Card.Header>
                  <Accordion.Collapse eventKey="0">
                    <Card.Body>
                      Description:{" "}
                      <textarea
                        cols={25}
                        rows={10}
                        placeholder={
                          "Describe any additional notes regarding this log"
                        }
                        id={"new-log-desc"}
                      />
                    </Card.Body>
                  </Accordion.Collapse>
                </Card>
              </Accordion>
              <div
                className={"d-flex d-inline-flex"}
                style={{ float: "right" }}
              >
                <Button
                  className={"p-2"}
                  variant={"outline-primary"}
                  onClick={(e) => {
                    e.preventDefault();
                    if (
                      document.querySelector("#new-log-title").value === "" ||
                      document.querySelector("#new-log-desc").value === ""
                    ) {
                      alert("All must be filled!");
                    }

                    console.log(
                      "LOG TITLE",
                      document.querySelector("#new-log-title").value
                    );
                    console.log(
                      "LOG DESC",
                      document.querySelector("#new-log-desc").value
                    );
                    let date = new Date();
                    //add new log
                    axios
                      .post(`api/logs/create`, {
                        username: user.email,
                        title: document.querySelector("#new-log-title").value,
                        description: document.querySelector("#new-log-desc")
                          .value,
                        date: date,
                      })
                      .then((response) => {
                        console.log(response);
                        window.location.reload();
                      })
                      .catch((err) => {
                        console.log(err);
                      });
                  }}
                >
                  Submit
                </Button>
                <Button
                  className={"ml-2 mr-2"}
                  variant={"outline-danger"}
                  onClick={(e) => {
                    e.preventDefault();
                    SetNewLog(null);
                  }}
                >
                  Cancel
                </Button>
              </div>
            </>
          );
        }}
      >
        <FcPlus size={"2em"} />
      </button>
      <br />

      <span className="ml-2 p-2 font-weight-bold" style={{ color: "white" }}>
        Add Log
      </span>
    </>
  );
};
export default ViewLogList;
