import React, { useState, useEffect } from "react";
import axios from "axios";
import { Button, Accordion, Card, Table } from "react-bootstrap";
import { FcPlus } from "react-icons/fc";
import { useAuth0 } from "@auth0/auth0-react";
import EditableTable from "../partials/EditableTable";
const ViewLogList = () => {
  const { user, isAuthenticated } = useAuth0();

  const [isShown, SetShown] = useState([]);
  const [isDisplayList, SetDisplayList] = useState([]);
  const [isNewExercise, SetNewExercise] = useState([]);
  const [isCurrID, SetCurrID] = useState([]);
  const [isNewLog, SetNewLog] = useState(null);
  const [isWorkoutDesc, SetWorkoutDesc] = useState([]);

  useEffect(() => {
    if (isAuthenticated) {
      fetchData();
    }
  }, []);

  const fetchData = () => {
    axios.get(`api/logs`).then((res) => {
      res.data.forEach((el, idx) => {
        if (el.username === user.email) {
          let currList = [];
          for (let attr in el) {
            if (el.hasOwnProperty(attr)) {
              if (attr === "_id")
                SetCurrID((isCurrID) => [...isCurrID, el[attr]]);
              currList.push({ key: attr, val: el[attr] });
            }
          }
          SetShown((isShown) => [...isShown, false]);
          SetDisplayList((isDisplayList) => [...isDisplayList, currList]);
        }
      });
    });
  };

  const SetExerciseDescription = (state, idx) => {
    SetWorkoutDesc((isWorkoutDesc) => [
      ...isWorkoutDesc.slice(0, idx),
      state,
      ...isWorkoutDesc.slice(idx + 1),
    ]);

    console.log(isWorkoutDesc[idx]);
  };
  const handleDelete = (e, idx) => {
    e.preventDefault();
    if (isWorkoutDesc.set.length > 1) {
      let newIndexArr = [];
      isWorkoutDesc.set.forEach((el, index) => {
        if (index > idx) {
          newIndexArr.push(el - 1);
        }
      });
      SetWorkoutDesc({
        set: [...isWorkoutDesc.set.slice(0, idx), ...newIndexArr],
        reps: [
          ...isWorkoutDesc.reps.slice(0, idx),
          ...isWorkoutDesc.reps.slice(idx + 1),
        ],
        weight: [
          ...isWorkoutDesc.weight.slice(0, idx),
          ...isWorkoutDesc.weight.slice(idx + 1),
        ],
      });
    } else {
      SetWorkoutDesc({
        set: [
          ...isWorkoutDesc.set.slice(0, idx),
          ...isWorkoutDesc.set.slice(idx + 1),
        ],
        reps: [
          ...isWorkoutDesc.reps.slice(0, idx),
          ...isWorkoutDesc.reps.slice(idx + 1),
        ],
        weight: [
          ...isWorkoutDesc.weight.slice(0, idx),
          ...isWorkoutDesc.weight.slice(idx + 1),
        ],
      });
    }
  };
  return (
    <>
      <div>
        {isDisplayList.map((el, index) => {
          return (
            <Accordion
              key={"log-disp" + index}
              className={"p-2"}
              defaultActiveKey="0"
            >
              <Card>
                {
                  <>
                    {Object.entries(el).map((log) => {
                      if (log[1].key === "title") {
                        return (
                          <Accordion.Toggle
                            as={Card.Header}
                            eventKey="0"
                            id={"log-title-" + `${index}`}
                          >
                            {log[1].val}
                          </Accordion.Toggle>
                        );
                      } else if (log[1].key === "description") {
                        return (
                          <Accordion.Collapse eventKey="0">
                            <Card.Body>
                              <div className={"p-2"}>
                                <p id={"log-desc-" + `${index}`}>
                                  {log[1].key === "description" && log[1].val}
                                </p>
                              </div>
                              <>
                                {Object.entries(el).map((log) => {
                                  if (log[1].key === "exercises") {
                                    return (
                                      <>
                                        {log[1].val.map((ex) => {
                                          return (
                                            <div
                                              className={"container"}
                                              style={{ overflowX: "auto" }}
                                            >
                                              <h3>{ex.name}</h3>
                                              <Table
                                                striped
                                                borderless
                                                variant={"dark"}
                                              >
                                                <thead className="thead-dark">
                                                  <tr id="table-thead-tr">
                                                    <th />
                                                    <th>Set</th>
                                                    <th>Reps</th>
                                                    <th>Weight</th>
                                                  </tr>
                                                </thead>
                                                <tbody>
                                                  {ex.set.map((set, idx) => {
                                                    return (
                                                      <tr>
                                                        <td></td>
                                                        <td>{set}</td>
                                                        <td>{ex.reps[idx]}</td>
                                                        <td>
                                                          {ex.weight[idx]}
                                                        </td>
                                                      </tr>
                                                    );
                                                  })}
                                                </tbody>
                                              </Table>
                                            </div>
                                          );
                                        })}
                                      </>
                                    );
                                  }
                                })}
                              </>
                              {isShown[index] && (
                                <>
                                  <div
                                    className={"p-2 ml-2 d-flex d-inline-flex"}
                                  >
                                    <input
                                      size={25}
                                      id={"new-workout-name" + `${index}`}
                                    />
                                  </div>
                                  <>
                                    <div>
                                      <EditableTable
                                        index={index}
                                        thead={["Set", "Reps", "Weight"]}
                                        SetData={SetExerciseDescription}
                                        style={{ color: "black" }}
                                      />
                                      <div
                                        className={"d-flex d-inline-flex"}
                                        style={{ float: "right" }}
                                      >
                                        <Button
                                          id={index}
                                          className={"p-2"}
                                          variant={"outline-primary"}
                                          onClick={(e) => {
                                            // e.preventDefault();
                                            let logTitle = document.getElementById(
                                              "log-title-" + `${e.target.id}`
                                            ).innerText;
                                            let exerciseName = document.querySelector(
                                              "#new-workout-name" +
                                                `${e.target.id}`
                                            ).value;
                                            let logDesc = document.getElementById(
                                              "log-desc-" + `${e.target.id}`
                                            ).innerText;
                                            if (
                                              exerciseName === null ||
                                              exerciseName === ""
                                            ) {
                                              alert("Please provide the name");
                                              return;
                                            }
                                            let date = new Date();
                                            console.log(
                                              "log title:",
                                              logTitle,
                                              "exercise: ",
                                              exerciseName,
                                              "logDesc",
                                              logDesc,
                                              isWorkoutDesc
                                            );
                                            axios
                                              .post(
                                                `api/logs/add/${
                                                  isCurrID[e.target.id]
                                                }`,
                                                {
                                                  username: user.email,
                                                  title: logTitle,
                                                  name: exerciseName,
                                                  description: logDesc,
                                                  set:
                                                    isWorkoutDesc[e.target.id]
                                                      .set,
                                                  reps:
                                                    isWorkoutDesc[e.target.id]
                                                      .reps,
                                                  weight:
                                                    isWorkoutDesc[e.target.id]
                                                      .weight,
                                                  date: date,
                                                }
                                              )
                                              .then((response) => {
                                                console.log(response);
                                              })
                                              .catch((err) => {
                                                console.log(err);
                                              });
                                            window.location.reload();
                                          }}
                                        >
                                          Submit
                                        </Button>
                                        <Button
                                          className={"ml-2"}
                                          variant={"outline-danger"}
                                          onClick={(e) => {
                                            e.preventDefault();
                                            SetNewExercise((isNewExercise) => [
                                              ...isNewExercise.slice(0, index),
                                              ...isNewExercise.slice(index + 1),
                                            ]);
                                            SetShown((isShown) => [
                                              ...isShown.slice(0, index),
                                              false,
                                              ...isShown.slice(index + 1),
                                            ]);
                                          }}
                                        >
                                          Cancel
                                        </Button>
                                      </div>
                                    </div>
                                  </>
                                </>
                              )}
                              <div
                                className={"d-flex d-inline-flex"}
                                style={{ textAlign: "center" }}
                              >
                                <button
                                  id={index}
                                  style={{
                                    borderRadius: "10vw",
                                    background: "none",
                                    borderStyle: "hidden",
                                  }}
                                  onClick={(e) => {
                                    e.preventDefault();

                                    SetShown((isShown) => [
                                      ...isShown.slice(0, index),
                                      true,
                                      ...isShown.slice(index + 1),
                                    ]);
                                  }}
                                >
                                  <FcPlus size={"2em"} />
                                </button>
                                <br />
                                <span
                                  className="p-2 font-weight-bold"
                                  style={{ color: "black" }}
                                >
                                  Add New Exercise
                                </span>
                              </div>
                            </Card.Body>
                          </Accordion.Collapse>
                        );
                      }
                    })}
                  </>
                }
              </Card>

              <Card>
                {Object.entries(el).map((log) => {
                  if (log[1].key === "date") {
                    return log[1].val;
                  }
                })}
              </Card>
            </Accordion>
          );
        })}
      </div>
      {isNewLog}
      <button
        style={{
          borderRadius: "10vw",
          background: "none",
          borderStyle: "hidden",
        }}
        onClick={(e) => {
          e.preventDefault();
          SetNewLog(
            <>
              <Accordion className={"p-2"} defaultActiveKey="0">
                <Card>
                  <Card.Header>
                    Title: <input id={"new-log-title"} />
                  </Card.Header>
                  <Accordion.Collapse eventKey="0">
                    <Card.Body>
                      Description:{" "}
                      <textarea
                        cols={25}
                        rows={10}
                        placeholder={
                          "Describe any additional notes regarding this log"
                        }
                        id={"new-log-desc"}
                      />
                    </Card.Body>
                  </Accordion.Collapse>
                </Card>
              </Accordion>
              <div
                className={"d-flex d-inline-flex"}
                style={{ float: "right" }}
              >
                <Button
                  className={"p-2"}
                  variant={"outline-primary"}
                  onClick={(e) => {
                    e.preventDefault();
                    if (
                      document.querySelector("#new-log-title").value === "" ||
                      document.querySelector("#new-log-desc").value === ""
                    ) {
                      alert("All must be filled!");
                    }

                    console.log(
                      "LOG TITLE",
                      document.querySelector("#new-log-title").value
                    );
                    console.log(
                      "LOG DESC",
                      document.querySelector("#new-log-desc").value
                    );
                    let date = new Date();
                    //add new log
                    axios
                      .post(`api/logs/create`, {
                        username: user.email,
                        title: document.querySelector("#new-log-title").value,
                        description: document.querySelector("#new-log-desc")
                          .value,
                        date: date,
                      })
                      .then((response) => {
                        window.location.reload();
                      })
                      .catch((err) => {
                        console.log(err);
                      });
                  }}
                >
                  Submit
                </Button>
                <Button
                  className={"ml-2 mr-2"}
                  variant={"outline-danger"}
                  onClick={(e) => {
                    e.preventDefault();
                    SetNewLog(null);
                  }}
                >
                  Cancel
                </Button>
              </div>
            </>
          );
        }}
      >
        <FcPlus size={"2em"} />
      </button>
      <br />

      <span className="ml-2 p-2 font-weight-bold" style={{ color: "white" }}>
        Add Log
      </span>
    </>
  );
};
export default ViewLogList;
