import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import SubmitEditLog from "./SubmitEditLog";
import CustomTable from "../../../partials/CustomTable";
import update from "immutability-helper";
import { isMobile } from "react-device-detect";
const EditLogList = ({ el, index, isEdit, isCurrID, updateData, SetEdit }) => {
  const [isEditList, SetEditList] = useState(el);
  return Object.entries(el).map((log) => {
    if (log[1].key === "exercises") {
      return (
        <>
          {log[1].val.map((ex, li) => {
            return (
              <div className={"container"}>
                <div
                  className={"d-flex d-inline-flex"}
                  style={{ overflowX: "scroll" }}
                >
                  <h5>Name: </h5>
                </div>
                <br />
                <div className={"d-flex d-inline-flex"}>
                  <input
                    id={li}
                    type="text"
                    size={isMobile ? 20 : 30}
                    placeholder={ex.name}
                    onChange={(e) => {
                      const index = Number(e.target.id);
                      const val = e.target.value;
                      SetEditList((isEditList) =>
                        update(isEditList, {
                          0: {
                            val: {
                              [index]: { name: { $set: val } },
                            },
                          },
                        })
                      );
                    }}
                  />
                </div>
                <hr />
                <div className={"d-flex d-inline-flex"}>
                  <h5>Description:</h5>
                  <br />
                </div>
                <div
                  className={"d-flex d-inline-flex"}
                  style={{ overflowX: "scroll" }}
                >
                  <textarea
                    id={li}
                    className={"mb-2"}
                    placeholder={ex.description}
                    cols={isMobile ? 25 : 50}
                    rows={10}
                    onChange={(e) => {
                      const index = Number(e.target.id);
                      let val = e.target.value;
                      SetEditList((isEditList) =>
                        update(isEditList, {
                          0: {
                            val: {
                              [index]: {
                                description: { $set: val },
                              },
                            },
                          },
                        })
                      );
                    }}
                  />
                </div>
                <hr />
                <CustomTable
                  index={li}
                  thead={["Set", "Reps", "Weight"]}
                  data={[ex.set, ex.reps, ex.weight]}
                  SetNewList={SetEditList}
                  isNewList={isEditList}
                  warning={false}
                  editable={true}
                  style={{ fontSize: "1.5vh" }}
                />
                <hr />
              </div>
            );
          })}
          <hr />
          {isEdit[index] && (
            <SubmitEditLog
              index={index}
              isCurrID={isCurrID}
              isEdit={isEdit}
              SetEdit={SetEdit}
              isEditList={isEditList}
              updateData={updateData}
            />
          )}
        </>
      );
    }
  });
};
export default EditLogList;
