import React from "react";
import { Table } from "react-bootstrap";

const ViewWorkoutList = ({ el }) => {
  return Object.entries(el).map((log) => {
    if (log[1].key === "exercises") {
      return (
        <>
          {log[1].val.map((ex) => {
            return (
              <div className={"container"} style={{ overflowX: "auto" }}>
                <h3>{ex.name}</h3>
                <p>{ex.description}</p>
                <Table striped borderless variant={"dark"}>
                  <thead className="thead-dark">
                    <tr id="table-thead-tr">
                      <th />
                      <th>Set</th>
                      <th>Reps</th>
                      <th>Weight</th>
                    </tr>
                  </thead>
                  <tbody>
                    {ex.set.map((set, idx) => {
                      return (
                        <tr>
                          <td></td>
                          <td>{set}</td>
                          <td>{ex.reps[idx]}</td>
                          <td>{ex.weight[idx]}</td>
                        </tr>
                      );
                    })}
                  </tbody>
                </Table>
                <hr />
              </div>
            );
          })}
        </>
      );
    }
  });
};
export default ViewWorkoutList;
