import React from "react";
import { Button } from "react-bootstrap";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
const SubmitEditLog = ({
  index,
  isEditList,
  isCurrID,
  updateData,
  SetEdit,
}) => {
  const { user } = useAuth0();
  return (
    <>
      <Button
        className={"p-2"}
        variant={"outline-primary"}
        onClick={(e) => {
          e.preventDefault();
          let date = new Date();
          let logTitle = document.querySelector(`#edit-log-title-${index}`);
          let logDesc = document.querySelector(`#edit-log-desc-${index}`);
          logTitle =
            logTitle.value === null || logTitle.value.length === 0
              ? logTitle.getAttribute("placeholder")
              : logTitle.value;
          logDesc =
            logDesc.value === null || logDesc.value.length === 0
              ? logDesc.getAttribute("placeholder")
              : logDesc.value;
          //edit the log
          axios
            .patch(`api/logs/update/${isCurrID[index]}`, {
              username: user.email,
              title: logTitle,
              description: logDesc,
              exercises: isEditList[0].val,
              date: date,
            })
            .then((response) => {
              console.log("Log Updated");
              SetEdit(false);
              updateData(true, isCurrID[index]);
            })
            .catch((err) => {
              console.log(err);
            });
        }}
      >
        Save
      </Button>
      <Button
        className={"ml-2 mr-2"}
        variant={"outline-danger"}
        onClick={(e) => {
          e.preventDefault();
          // SetNewLog(null);
        }}
      >
        Cancel
      </Button>
    </>
  );
};
export default SubmitEditLog;
