import React, { useState, useEffect, createRef } from "react";
import axios from "axios";
import { Accordion, Button, Card } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";
import SetNewWorkout from "./SetNewWorkout";
import ViewWorkoutList from "./ViewWorkoutList";
import SetNewLog from "./SetNewLog";
import RemoveLog from "./RemoveLog";
import RemoveLogList from "./RemoveLogList";
import { BrowserView, MobileView } from "react-device-detect";
import Swipe from "react-easy-swipe";
import update from "immutability-helper";

const ViewLogList = () => {
  const { user, isAuthenticated } = useAuth0();
  const [isShown, SetShown] = useState([]);
  const [isDisplayList, SetDisplayList] = useState([]);
  const [isCurrID, SetCurrID] = useState([]);
  const [isDeleteList, SetDeleteList] = useState([]);
  const [isDelete, SetDelete] = useState(false);
  const [isSwipedRight, SetOnSwipeRight] = useState(false);
  const [isSwipeEnd, SetSwipeOnEnd] = useState(false);
  useEffect(() => {
    if (isAuthenticated) {
      fetchData();
    }
  }, []);

  const fetchData = (newLog = false, newWorkout = null) => {
    if (!newLog && newWorkout === null) {
      axios.get(`api/logs`).then((res) => {
        res.data.forEach((el, idx) => {
          if (el.username === user.email) {
            let currList = [];
            for (let attr in el) {
              if (el.hasOwnProperty(attr)) {
                if (attr === "_id")
                  SetCurrID((isCurrID) => [...isCurrID, el[attr]]);
                currList.push({ key: attr, val: el[attr] });
              }
            }
            SetShown((isShown) => [...isShown, false]);
            SetDeleteList((isDeleteList) => [...isDeleteList, createRef()]);
            SetDisplayList((isDisplayList) => [...isDisplayList, currList]);
          }
        });
      });
    } else if (newLog && newWorkout === null) {
      axios.get(`api/logs/`).then((res) => {
        res.data.forEach((el, idx) => {
          if (el.username === user.email) {
            if (!isCurrID.includes(el._id)) {
              let currList = [];
              for (let attr in el) {
                if (el.hasOwnProperty(attr)) {
                  if (attr === "_id")
                    SetCurrID((isCurrID) => [...isCurrID, el[attr]]);
                  currList.push({ key: attr, val: el[attr] });
                }
              }
              SetShown((isShown) => [...isShown, false]);
              SetDeleteList((isDeleteList) => [...isDeleteList, createRef()]);
              SetDisplayList((isDisplayList) => [...isDisplayList, currList]);
            }
          }
        });
      });
    } else {
      axios
        .get(`api/logs/${newWorkout}`)
        .then((res) => {
          console.log(res.data);
          let newExerciseList = [];
          newExerciseList = res.data.exercises;

          let logLocation = isCurrID.indexOf(newWorkout);
          console.log(logLocation);
          SetDisplayList((isDisplayList) => [
            ...isDisplayList.slice(0, logLocation),
            update(isDisplayList[logLocation], {
              0: { $set: { key: "exercises", val: newExerciseList } },
            }),
            ...isDisplayList.slice(logLocation + 1),
          ]);
        })
        .catch((err) => {
          console.log(err);
        });
    }
  };

  const onDelete = () => {
    SetDelete(!isDelete);
  };

  const onSwipeRight = (pos, event) => {
    if (isDelete) {
      console.log(`Moved to the right`, event);
      SetOnSwipeRight(true);
    }
  };
  const onSwipeEnd = (event) => {
    if (isSwipedRight) {
      handleDelete(Number(event.target.id.replace(/log-title-/g, "")));
    }
  };

  const handleDelete = (index) => {
    isDeleteList[index].current.classList.add(
      "animate__animated",
      "animate__fadeOutRight"
    );
    isDeleteList[index].current.addEventListener("animationend", () => {
      axios
        .delete(`api/logs/${isCurrID[index]}`)
        .then((res) => {
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
      SetCurrID((isCurrID) => [
        ...isCurrID.slice(0, index),
        null,
        ...isCurrID.slice(index + 1),
      ]);
      SetDisplayList((isDisplayList) => [
        ...isDisplayList.slice(0, index),
        null,
        ...isDisplayList.slice(index + 1),
      ]);
      SetDeleteList((isDeleteList) => [
        ...isDeleteList.slice(0, index),
        null,
        ...isDeleteList.slice(index + 1),
      ]);
    });
    SetOnSwipeRight(false);
    SetSwipeOnEnd(false);
  };

  return (
    <>
      <div>
        {isDisplayList.map((el, index) => {
          if (el === null) {
            return <></>;
          }
          return (
            <Swipe
              onSwipeRight={onSwipeRight}
              onSwipeEnd={onSwipeEnd}
              allowMouseEvents={true}
              key={"log-disp" + index}
            >
              <Accordion className={"p-2"} ref={isDeleteList[index]}>
                <Card>
                  {
                    <>
                      <>
                        <RemoveLogList
                          isDeleteList={isDelete}
                          index={index}
                          deleteData={handleDelete}
                        />
                      </>
                      {Object.entries(el).map((log) => {
                        if (log[1].key === "title") {
                          return (
                            <>
                              <BrowserView>
                                <Accordion.Toggle
                                  as={Card.Header}
                                  eventKey="0"
                                  id={"log-title-" + `${index}`}
                                  style={{
                                    transition: "1s",
                                    paddingLeft: !isDelete ? "1vw" : "6vw",
                                  }}
                                >
                                  {log[1].val}
                                </Accordion.Toggle>
                              </BrowserView>
                              <MobileView>
                                <Accordion.Toggle
                                  as={Card.Header}
                                  eventKey="0"
                                  id={"log-title-" + `${index}`}
                                  style={{
                                    transition: "1s",
                                    paddingLeft: !isDelete ? "1vw" : "13vw",
                                  }}
                                >
                                  {log[1].val}
                                </Accordion.Toggle>
                              </MobileView>
                            </>
                          );
                        } else if (log[1].key === "description") {
                          return (
                            <Accordion.Collapse eventKey="0">
                              <Card.Body>
                                <div className={"p-2"}>
                                  <p id={"log-desc-" + `${index}`}>
                                    {log[1].key === "description" && log[1].val}
                                  </p>
                                </div>
                                <>
                                  <ViewWorkoutList el={el} />
                                </>
                                <SetNewWorkout
                                  index={index}
                                  isShown={isShown}
                                  SetShown={SetShown}
                                  isCurrID={isCurrID}
                                  updateData={fetchData}
                                />
                              </Card.Body>
                            </Accordion.Collapse>
                          );
                        }
                      })}
                    </>
                  }
                </Card>

                <Card>
                  {Object.entries(el).map((log) => {
                    if (log[1].key === "date") {
                      let date = new Date(log[1].val);
                      return date.toDateString();
                    }
                  })}
                </Card>
              </Accordion>
            </Swipe>
          );
        })}
      </div>
      <div className={"d-inline-start"}>
        <SetNewLog updateData={fetchData} />
        <RemoveLog isDelete={isDelete} onDelete={onDelete} />
      </div>
    </>
  );
};
export default ViewLogList;
