import React, { useEffect, useState } from "react";
import { Button } from "react-bootstrap";
import Delete from "@material-ui/icons/Delete";
import { wobble } from "react-animations";
import styled, { keyframes } from "styled-components";
import { BrowserView, MobileView } from "react-device-detect";

const RemoveLogList = ({ isDeleteList, index, deleteData }) => {
  const Wobble = styled.div`
    animation: 1s ${keyframes`${wobble}`} infinite;
  `;

  return (
    <>
      <BrowserView>
        <div className="position-relative">
          <div
            className={"position-absolute"}
            style={{
              transition: "1s",
              left: !isDeleteList ? "-10vw" : "0vw",
              background: "#ffe6e6",
              width: "5vw",
              borderRadius: "0.2vw",
            }}
          >
            <Wobble>
              <button
                className={"trans-button p-2"}
                style={{
                  borderRadius: "10vw",
                }}
                onClick={(e) => {
                  deleteData(index);
                }}
              >
                <Delete color="secondary" />
              </button>
            </Wobble>
          </div>
        </div>
      </BrowserView>
      <MobileView>
        <div className="position-relative">
          <div
            className={"position-absolute"}
            style={{
              transition: "1s",
              left: !isDeleteList ? "-12vw" : "0vw",
              background: "#ffe6e6",
              width: "11vw",
              borderRadius: "0.2vw",
            }}
          >
            <Wobble>
              <button
                className={"trans-button p-2"}
                style={{
                  borderRadius: "10vw",
                }}
                onClick={(e) => {
                  deleteData(index);
                }}
              >
                <Delete color="secondary" />
              </button>
            </Wobble>
          </div>
        </div>
      </MobileView>
    </>
  );
};
export default RemoveLogList;
