import React, { useState } from "react";
import { Accordion, Card } from "react-bootstrap";
import SubmitLog from "./SubmitLog";
import { FcPlus } from "react-icons/fc";

const SetNewLog = ({ updateData }) => {
  const [isNewLog, SetNewLog] = useState(null);
  return (
    <>
      {isNewLog}
      <div className={"d-flex d-inline-flex"}>
        <button
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            SetNewLog(
              <>
                <Accordion className={"p-2"} defaultActiveKey="0">
                  <Card>
                    <Card.Header>
                      Title: <input id={"new-log-title"} />
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                      <Card.Body>
                        Description:{" "}
                        <textarea
                          cols={25}
                          rows={10}
                          placeholder={
                            "Describe any additional notes regarding this log"
                          }
                          id={"new-log-desc"}
                        />
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                </Accordion>
                <div
                  className={"d-flex d-inline-start"}
                  style={{ float: "right" }}
                >
                  <SubmitLog SetNewLog={SetNewLog} updateData={updateData} />
                </div>
              </>
            );
          }}
        >
          <FcPlus size={"2em"} />
        </button>
        <br />
        <span className="ml-2 p-2 font-weight-bold" style={{ color: "white" }}>
          Add Log
        </span>
      </div>
    </>
  );
};
export default SetNewLog;
