import React, { useState } from "react";
import { Accordion, Card } from "react-bootstrap";
import SubmitNewLog from "./SubmitNewLog";
import { FcPlus } from "react-icons/fc";

const NewLogList = ({ updateData }) => {
  const [isNewLog, SetNewLog] = useState(null);
  return (
    <>
      {isNewLog}
      <div className={"d-flex d-inline-flex"}>
        <button
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            SetNewLog(
              <>
                <Accordion
                  className={"p-2"}
                  defaultActiveKey="0"
                  style={{ overflowX: "scroll" }}
                >
                  <Card>
                    <Card.Header>
                      Title: <input id={"new-log-title"} />
                    </Card.Header>
                    <Accordion.Collapse eventKey="0">
                      <Card.Body>
                        <div className={"d-flex d-inline-flex"}>
                          <div className={"pr-2"}>
                            Description: <br />
                          </div>
                          <textarea
                            cols={50}
                            rows={10}
                            placeholder={
                              "Describe any additional notes regarding this log"
                            }
                            id={"new-log-desc"}
                          />
                        </div>
                      </Card.Body>
                    </Accordion.Collapse>
                  </Card>
                </Accordion>
                <div
                  className={"d-flex d-inline-start"}
                  style={{ float: "right" }}
                >
                  <SubmitNewLog SetNewLog={SetNewLog} updateData={updateData} />
                </div>
              </>
            );
          }}
        >
          <>
            <FcPlus size={"2em"} />
            <span
              className="ml-2 p-2 font-weight-bold"
              style={{ color: "white" }}
            >
              Add Log
            </span>
          </>
        </button>
        <br />
      </div>
    </>
  );
};
export default NewLogList;
