import React, { useEffect, useState } from "react";
import { useLocation } from "react-router";
import { Link } from "react-router-dom";
import { Breadcrumb } from "react-bootstrap";
import { LinkContainer } from "react-router-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";

const BreadCrumbs = ({ location }) => {
  const { user } = useAuth0();
  const [isPath, SetPath] = useState(location.pathname.split("/"));
  useEffect(() => {
    if (location.state !== undefined) {
      let newlocation = location.pathname.split("/");
      if (location.state.logTitle !== undefined)
        newlocation.push(location.state.logTitle);
      SetPath(newlocation);
    } else {
      SetPath(location.pathname.split("/"));
    }
  }, [location]);
  return (
    <Breadcrumb>
      {isPath.map((el, idx) => {
        if (idx === 0) {
          return <Breadcrumb.Item href="/">Home</Breadcrumb.Item>;
        }
        if (idx === isPath.length - 1) {
          return (
            <Breadcrumb.Item active href={`/${location.pathname}`}>
              {el}
            </Breadcrumb.Item>
          );
        }
        let copyArr = isPath.slice(0, isPath.length - 1);
        return (
          <LinkContainer to={`${copyArr.join("/")}`}>
            <Breadcrumb.Item>{el}</Breadcrumb.Item>
          </LinkContainer>
        );
      })}
    </Breadcrumb>
  );
};
export default BreadCrumbs;
