import React, { useState } from "react";
import { MdRemoveCircle, MdDone } from "react-icons/md";
import { BrowserView, MobileView } from "react-device-detect";
const RemoveLog = ({ isDelete, onDelete }) => {
  return (
    <>
      <div className={"d-flex d-inline-flex"}>
        <button
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
            borderColor: "none",
          }}
          onClick={onDelete}
        >
          {!isDelete ? (
            <MdRemoveCircle
              size={"2em"}
              style={{ color: "red", outline: "none" }}
            />
          ) : (
            <MdDone size={"2em"} style={{ color: "orange", outline: "none" }} />
          )}
        </button>
        <br />
        <span className="ml-2 p-2 font-weight-bold" style={{ color: "white" }}>
          {!isDelete ? "Remove Log" : "Done?"}
        </span>
      </div>
    </>
  );
};
export default RemoveLog;
