import React, { useEffect, useState } from "react";
import { Table } from "react-bootstrap";
import ToggleEdit from "../../partials/ToggleEdit";
import CustomTable from "../../partials/CustomTable";
import SubmitUpdate from "../../partials/SubmitUpdate";
import EditWorkout from "./actions/edit/EditWorkout";
import RemoveLogList from "../actions/remove/RemoveLogList";
import RemoveWorkout from "./actions/remove/RemoveWorkout";

const ViewWorkoutList = ({ el, isCurrID, updateData, logIndex, user }) => {
  const [isEdit, SetEdit] = useState([]);
  const [isDelete, SetDelete] = useState([]);
  const [isEditList, SetEditList] = useState(el);
  useEffect(() => {
    for (let i = 0; i < el[0].val.length; i++) {
      SetEdit((isEdit) => [...isEdit, false]);
      SetDelete((isDelete) => [...isDelete, false]);
    }
  }, []);
  return Object.entries(el).map((log) => {
    if (log[1].key === "exercises") {
      return (
        <>
          {log[1].val.map((ex, index) => {
            return (
              <div className={"container"} style={{ overflowX: "auto" }}>
                {!isEdit[index] ? (
                  <>
                    <h3>{ex.name}</h3>
                    <p>{ex.description}</p>
                    <Table striped borderless variant={"dark"}>
                      <thead className="thead-dark">
                        <tr id="table-thead-tr">
                          <th />
                          <th>Set</th>
                          <th>Reps</th>
                          <th>Weight</th>
                        </tr>
                      </thead>
                      <tbody>
                        {ex.set.map((set, idx) => {
                          return (
                            <tr>
                              <td></td>
                              <td>{set}</td>
                              <td>{ex.reps[idx]}</td>
                              <td>{ex.weight[idx]}</td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </Table>
                  </>
                ) : (
                  <>
                    <EditWorkout
                      SetEditList={SetEditList}
                      isEditList={isEditList}
                      li={index}
                      ex={ex}
                    />
                    <CustomTable
                      index={index}
                      thead={["Set", "Reps", "Weight"]}
                      data={[ex.set, ex.reps, ex.weight]}
                      SetNewList={SetEditList}
                      isNewList={isEditList}
                      editable={true}
                      style={{ fontSize: "1.5vh" }}
                    />
                    <SubmitUpdate
                      index={logIndex}
                      workoutIndex={index}
                      isCurrID={isCurrID}
                      isEdit={isEdit}
                      SetEdit={SetEdit}
                      isEditList={isEditList}
                      updateData={updateData}
                    />
                  </>
                )}

                <hr />
                <div style={{ fontSize: "1.5vh" }}>
                  <RemoveWorkout
                    index={index}
                    logIndex={logIndex}
                    isCurrID={isCurrID}
                    SetEditList={SetEditList}
                    isEditList={isEditList}
                    updateData={updateData}
                    textColor={{ color: "black" }}
                    user={user}
                  />
                  <ToggleEdit
                    isEdit={isEdit}
                    SetEdit={SetEdit}
                    index={index}
                    target={"Exercise"}
                    multiple={true}
                  />
                </div>

                <hr />
              </div>
            );
          })}
        </>
      );
    }
  });
};
export default ViewWorkoutList;
