import { PURGE } from "redux-persist";

const INIT_STATE = {
  isSignedIn: null,
  userId: null,
};

export default (state = INIT_STATE, action) => {
  switch (action.type) {
    case "SIGN_IN":
      return { ...state, isSignedIn: true, userId: action.payload };
    case "SIGN_OUT":
      return { ...state, isSignedIn: false, userId: null };
    case PURGE:
      return INIT_STATE;
    default:
      return state;
  }
};
