import React, { useState } from "react";
import { Button, Table } from "react-bootstrap";
import Delete from "@material-ui/icons/Delete";
import { wobble } from "react-animations";
import styled, { keyframes } from "styled-components";
import update from "immutability-helper";
import { FcPlus } from "react-icons/fc";
const CustomTable = ({
  index,
  thead,
  data,
  SetNewList,
  isNewList = [],
  SetRemoval = false,
  style,
  editable = false,
}) => {
  const Wobble = styled.div`
    animation: 1s ${keyframes`${wobble}`} infinite;
  `;

  const [isSelected, SetSelect] = useState(false);
  const [isSelectList, SetSelectList] = useState([]);
  const [isData, SetData] = useState(data);

  const toggleAll = () => {
    let parent = document.getElementById("checkbox-parent");
    let children = document.getElementsByName("checkbox-children");
    children.forEach((el) => {
      el.checked = parent.checked;
      if (parent.checked) {
        SetSelect(true);
        el.parentElement.parentElement.setAttribute(
          "style",
          "text-decoration: line-through; opacity:0.5"
        );
        SetSelectList((isSelectList) => [
          ...isSelectList,
          el.parentElement.parentElement,
        ]);
      } else {
        SetSelect(false);
        el.parentElement.parentElement.setAttribute(
          "style",
          "text-decoration: none; opacity:1;"
        );
        SetSelectList([]);
      }
    });
  };
  const showRemoveIcon = (e) => {
    let children = document.getElementsByName("checkbox-children");
    let selected = 0;
    children.forEach((el) => {
      if (el.checked) selected += 1;
    });
    if (selected > 0) SetSelect(true);
    else SetSelect(false);
    document.getElementById("checkbox-parent").checked =
      selected === children.length;

    let id = e.target.id;
    let rowEl = e.target.parentElement.parentElement;
    if (e.target.checked === true) {
      rowEl.setAttribute("style", "text-decoration: line-through; opacity:0.5");
      SetSelectList((isSelectList) => [...isSelectList, id]);
    } else {
      rowEl.setAttribute("style", "text-decoration: none; opacity:1;");
      let array = [...isSelectList]; // make a separate copy of the array
      let index = array.indexOf(rowEl);
      if (index !== -1) {
        array = [...array.slice(0, index), ...array.slice(index + 1)];
        SetSelectList(array);
      }
    }
  };
  const getSelectedForRemoval = () => {
    let currList = [];
    isData.forEach((data) => {
      currList.push(
        data.filter(function (item, index) {
          if (!isSelectList.includes(String(index))) return item;
        })
      );
    });
    SetNewList((isNewList) =>
      update(isNewList, {
        0: {
          val: {
            [index]: {
              set: { $set: currList[0] },
              reps: { $set: currList[1] },
              weight: { $set: currList[2] },
            },
          },
        },
      })
    );
    SetData(currList);
  };

  const addMoreRows = () => {
    let newRow = [
      isData[0][isData[0].length - 1] + 1,
      isData[1][isData[1].length - 1],
      isData[2][isData[2].length - 1],
    ];
    SetNewList((isNewList) =>
      update(isNewList, {
        0: {
          val: {
            [index]: {
              set: { $push: [newRow[0]] },
              reps: { $push: [newRow[1]] },
              weight: { $push: [newRow[2]] },
            },
          },
        },
      })
    );
    SetData((isData) =>
      update(isData, {
        0: {
          $push: [newRow[0]],
        },
        1: {
          $push: [newRow[1]],
        },
        2: {
          $push: [newRow[2]],
        },
      })
    );
  };

  let theadArr = [];
  for (let key in thead) {
    if (thead.hasOwnProperty(key)) {
      theadArr.push(<th key={key}>{thead[key]}</th>);
    } else {
      theadArr.push(<th key={key}>{key}</th>);
    }
  }
  return (
    <>
      <Table
        striped
        responsive
        bordered
        style={style !== undefined ? style : { color: "black" }}
      >
        <thead className="thead-dark">
          <tr id="table-thead-tr">
            <th>
              <input type="checkbox" id="checkbox-parent" onClick={toggleAll} />
            </th>
            {theadArr}
          </tr>
        </thead>
        <tbody>
          {isData[0].map((el, idx) => {
            return (
              <tr key={"custom-tr" + el + idx}>
                <td>
                  <input
                    id={idx}
                    type="checkbox"
                    defaultChecked={false}
                    name="checkbox-children"
                    onClick={(e) => {
                      showRemoveIcon(e);
                    }}
                  />
                </td>
                <>
                  <>
                    {!editable ? (
                      <>
                        <td key={el + idx}>{el}</td>
                      </>
                    ) : (
                      <>
                        <td key={idx}>
                          <input type="numeric" placeholder={el} size={3} />
                        </td>
                        <td key={idx}>
                          <input
                            type="numeric"
                            placeholder={isData[1][idx]}
                            size={3}
                          />
                        </td>
                        <td key={idx}>
                          <input
                            type="numeric"
                            placeholder={isData[2][idx]}
                            size={3}
                          />
                        </td>
                      </>
                    )}
                  </>
                </>
              </tr>
            );
          })}
        </tbody>
      </Table>

      {isSelected && (
        <div className="d-flex">
          <Wobble>
            <Button
              variant="outline-light"
              style={{ borderRadius: "10vw" }}
              onClick={getSelectedForRemoval}
            >
              <Delete color="secondary" />
            </Button>
          </Wobble>
        </div>
      )}
      <div className={"d-flex"} style={{ textAlign: "center" }}>
        <button
          id={index}
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            addMoreRows();
          }}
        >
          <FcPlus size={"2em"} />
          <span className="p-2 font-weight-bold" style={{ color: "black" }}>
            Add New Set
          </span>
        </button>
        <br />
      </div>
    </>
  );
};
export default CustomTable;
