import React, { useEffect, useState } from "react";
import { Button, Table } from "react-bootstrap";
import Delete from "@material-ui/icons/Delete";
import NumericInput from "react-numeric-input";
import { FcPlus } from "react-icons/fc";
import { FaMinusCircle } from "react-icons/fa";

const EditableTable = ({ index, thead, SetData, style }) => {
  const [isDesc, SetDesc] = useState({
    set: [1],
    reps: [1],
    weight: [5],
  });

  useEffect(() => {
    SetData(
      {
        set: isDesc.set,
        reps: isDesc.reps,
        weight: isDesc.weight,
      },
      index
    );
  }, [isDesc]);
  let theadArr = [];
  for (let key in thead) {
    if (thead.hasOwnProperty(key)) {
      theadArr.push(<th key={key}>{thead[key]}</th>);
    } else {
      theadArr.push(<th key={key}>{key}</th>);
    }
  }

  const handleDelete = (e, idx) => {
    e.preventDefault();
    if (isDesc.set.length > 1) {
      let newIndexArr = [];
      isDesc.set.forEach((el, index) => {
        if (index > idx) {
          newIndexArr.push(el - 1);
        }
      });
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...newIndexArr],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    } else {
      SetDesc({
        set: [...isDesc.set.slice(0, idx), ...isDesc.set.slice(idx + 1)],
        reps: [...isDesc.reps.slice(0, idx), ...isDesc.reps.slice(idx + 1)],
        weight: [
          ...isDesc.weight.slice(0, idx),
          ...isDesc.weight.slice(idx + 1),
        ],
      });
    }
  };
  return (
    <div className={"container"} style={{ overflowX: "auto" }}>
      <Table
        className={"editable-table"}
        responsive
        striped
        borderless
        variant={"dark"}
        style={style !== undefined ? style : { color: "white" }}
        onChange={() => {
          SetData({
            set: isDesc.set,
            reps: isDesc.reps,
            weight: isDesc.weight,
          });
        }}
      >
        <thead className="thead-dark">
          <tr id="table-thead-tr">
            <th />
            {theadArr}
          </tr>
        </thead>
        <tbody>
          {isDesc.set.map((el, idx) => {
            return (
              <tr key={"set-row" + idx}>
                <td>
                  <button
                    style={{
                      borderRadius: "10vw",
                      background: "none",
                      borderStyle: "hidden",
                    }}
                    onClick={(e) => handleDelete(e, idx)}
                  >
                    <FaMinusCircle size={"1em"} style={{ color: "red" }} />
                  </button>
                </td>
                <>
                  <td>
                    <NumericInput
                      size={1}
                      key={"set-content" + idx}
                      id={"set-" + idx}
                      min={1}
                      max={100}
                      value={isDesc.set > 100 ? 1 : isDesc.set[idx]}
                      style={{
                        input: {
                          color: "blue",
                        },
                      }}
                      onChange={(e) => {
                        SetDesc({
                          set: [
                            ...isDesc.set.slice(0, idx),
                            e,
                            ...isDesc.set.slice(idx + 1),
                          ],
                          reps: isDesc.reps,
                          weight: isDesc.weight,
                        });
                      }}
                    />
                  </td>
                </>
                <>
                  <td>
                    <NumericInput
                      size={1}
                      key={"workout-desc" + idx}
                      min={1}
                      max={100}
                      value={isDesc.reps > 100 ? 1 : isDesc.reps[idx]}
                      style={{
                        input: {
                          color: "blue",
                        },
                      }}
                      onChange={(e) => {
                        SetDesc({
                          set: isDesc.set,
                          reps: [
                            ...isDesc.reps.slice(0, idx),
                            e,
                            ...isDesc.reps.slice(idx + 1),
                          ],
                          weight: isDesc.weight,
                        });
                      }}
                    />
                  </td>
                </>
                <>
                  <td>
                    <NumericInput
                      size={3}
                      key={"workout-desc" + idx}
                      step={5.0}
                      min={0}
                      precision={1}
                      max={2000}
                      value={isDesc.weight > 2000 ? 2000 : isDesc.weight[idx]}
                      style={{
                        input: {
                          color: "green",
                        },
                      }}
                      onChange={(e) => {
                        SetDesc({
                          set: isDesc.set,
                          reps: isDesc.reps,
                          weight: [
                            ...isDesc.weight.slice(0, idx),
                            e,
                            ...isDesc.weight.slice(idx + 1),
                          ],
                        });
                      }}
                    />
                  </td>
                </>
              </tr>
            );
          })}
        </tbody>
      </Table>
      <div style={{ textAlign: "center" }}>
        <button
          style={{
            margin: "auto",
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            if (isDesc.set.length > 0) {
              SetDesc({
                set: [...isDesc.set, isDesc.set.slice(-1)[0] + 1],
                reps: [...isDesc.reps, 1],
                weight: [...isDesc.weight, 5],
              });
            } else {
              SetDesc({
                set: [...isDesc.set, 1],
                reps: [...isDesc.reps, 1],
                weight: [...isDesc.weight, 5],
              });
            }
          }}
        >
          <FcPlus size={"2em"} />
        </button>
      </div>
    </div>
  );
};
export default EditableTable;
