import React, { useEffect, useState } from "react";
import Card from "react-bootstrap/Card";
import Pagination from "react-bootstrap/Pagination";
import Collapse from "react-bootstrap/Collapse";
import Tooltip from "react-bootstrap/Tooltip";
import OverlayTrigger from "react-bootstrap/OverlayTrigger";
import { useLocation } from "react-router-dom";
const data = require("../data/exerciseListData.json");

const ExerciseResults = () => {
  const location = useLocation();
  const pages = [];
  const [isOpen, setOpen] = useState(0);

  const processedData = data.map((el, index) => {
    return (
      <Card
        key={el.Exercise}
        onClick={() => {
          setOpen(index);
        }}
        aria-controls="collapse-text"
        aria-expanded={isOpen}
        style={{ background: `#24154a`, backgroundSize: "cover" }}
      >
        <Card.Body>
          <Card.Title>{el.Exercise}</Card.Title>
          <Collapse key={index} in={isOpen === index}>
            <div id="collapse-text">
              <ul style={{ listStyleType: "none" }}>
                <li>Muscle Group: {el["Muscle Group"]}</li>
                <li>Level: {el.Level}</li>
                <OverlayTrigger
                  placement="right"
                  delay={{ show: 250, hide: 400 }}
                  overlay={
                    <Tooltip id="button-tooltip">
                      <ul style={{ fontSize: "0.8em", paddingLeft: 0 }}>
                        <li>U–Upper Body</li>
                        <li>L–Lower Body</li>
                        <li>C–Core</li>
                      </ul>
                    </Tooltip>
                  }
                >
                  {({ ref, ...triggerHandler }) => (
                    <li {...triggerHandler}>
                      <span ref={ref} style={{ paddingRight: "2rem" }}>
                        U/L/C: {el["U/L/C"]}
                      </span>
                    </li>
                  )}
                </OverlayTrigger>
                <li>Push/Pull: {el["P / P"]}</li>
                <OverlayTrigger
                  placement="right"
                  delay={{ show: 250, hide: 400 }}
                  overlay={
                    <Tooltip id="button-tooltip">
                      <ul style={{ fontSize: "0.8em", paddingLeft: 0 }}>
                        <li>FW–Free Weights</li>
                        <li>C–Cables</li>
                        <li>M–Machine</li>
                      </ul>
                    </Tooltip>
                  }
                >
                  {({ ref, ...triggerHandler }) => (
                    <li {...triggerHandler}>
                      <span ref={ref} style={{ paddingRight: "2rem" }}>
                        Modality: {el.Modality}
                      </span>
                    </li>
                  )}
                </OverlayTrigger>
                <OverlayTrigger
                  placement="right"
                  delay={{ show: 250, hide: 400 }}
                  overlay={
                    <Tooltip id="button-tooltip">
                      <ul style={{ fontSize: "0.8em", paddingLeft: 0 }}>
                        <li>M–Multi-Joint Exercise</li>
                        <li>S–Single-Joint Exercise</li>
                      </ul>
                    </Tooltip>
                  }
                >
                  {({ ref, ...triggerHandler }) => (
                    <li {...triggerHandler}>
                      <span ref={ref} style={{ paddingRight: "2rem" }}>
                        Joint: {el.Joint}
                      </span>
                    </li>
                  )}
                </OverlayTrigger>
              </ul>
            </div>
          </Collapse>
        </Card.Body>
      </Card>
    );
  });
  const [isCurrPage, SetCurrPage] = useState(1);
  const [isPrevPage, SetPrevPages] = useState(0);
  const [isNextPage, SetNextPages] = useState(2);
  const [isPageRange, SetPageRange] = useState([1, 10]);
  const [isDisplayData, SetDisplayData] = useState(processedData.slice(0, 10));
  useEffect(() => {
    if (Number(location.pathname.match(/\d+/g)) === 0) SetCurrPage(1);
    else SetCurrPage(Number(location.pathname.match(/\d+/g)));
    SetPrevPages(isCurrPage - 1);
    SetNextPages(isCurrPage + 1);
    SetDisplayData(processedData.slice(isCurrPage * 10 - 10, isCurrPage * 10));
    let baseNum = Math.trunc(isCurrPage / 10) + 1;
    SetPageRange([baseNum * 10 - 9, baseNum * 10]);
  }, [isOpen, isCurrPage, location]);
  pages.push(
    <Pagination.First key={"page-1"} href={"/exlist/weight_lifting/1/"} />
  );
  if (isPageRange[0] - 1 > 0)
    pages.push(
      <Pagination.Prev
        onClick={() => {
          SetPageRange([isPageRange[0] - 10, isPageRange[1] - 10]);
        }}
      />
    );

  if (isPageRange[0] > 3) {
    pages.push(<Pagination.Ellipsis />);
  }
  for (let i = isPageRange[0]; i <= isPageRange[1]; i++) {
    if (
      i <=
      Math.trunc(processedData.length / 10) +
        (processedData.length % 10 >= 1 ? 1 : 0)
    ) {
      pages.push(
        <Pagination.Item
          key={i}
          href={`/exlist/weight_lifting/${i}/`}
          active={i === isCurrPage}
        >
          {i}
        </Pagination.Item>
      );
    }
  }

  if (
    isPageRange[1] <
    Math.trunc(processedData.length / 10) +
      (processedData.length % 10 >= 1 ? 1 : 0)
  ) {
    pages.push(<Pagination.Ellipsis key={"page-ellipsis"} />);
    pages.push(
      <Pagination.Next
        onClick={() => {
          SetPageRange([isPageRange[0] + 10, isPageRange[1] + 10]);
        }}
      />
    );
  }
  pages.push(
    <Pagination.Last
      href={`/exlist/weight_lifting/${
        Math.trunc(processedData.length / 10) +
        (processedData.length % 10 >= 1 ? 1 : 0)
      }/`}
    />
  );

  return (
    <div className="grid">
      <div className="col" id="ex-list-desc">
        {isDisplayData}
      </div>
      <Pagination size="sm">{pages}</Pagination>
    </div>
  );
};
export default ExerciseResults;
