import React from "react";
import { MdRemoveCircle } from "react-icons/md";
import axios from "axios";
import update from "immutability-helper";

const RemoveWorkout = ({
  index,
  logIndex,
  isEditList,
  isCurrID,
  updateData,
  SetEditList,
  textColor = null,
  user,
}) => {
  return (
    <>
      <div className={"d-flex d-inline-flex"}>
        <button
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
            borderColor: "none",
          }}
          onClick={(e) => {
            SetEditList((isEditList) =>
              update(isEditList, {
                0: {
                  val: {
                    $splice: [[index, 1]],
                  },
                },
              })
            );
            let date = new Date();
            // let logTitle = document.querySelector(`#log-title-${logIndex}`);
            // let logDesc = document.querySelector(`#log-desc-${logIndex}`);
            // logTitle = logTitle.innerText;
            // logDesc = logDesc.innerText;

            //edit the log
            axios
              .patch(`api/logs/update/${isCurrID[logIndex]}`, {
                username: user.email,
                title: isEditList[3].val,
                description: isEditList[4].val,
                exercises: isEditList[0].val,
                date: date,
              })
              .then((response) => {
                console.log("Log Updated");
                updateData(true, isCurrID[logIndex]);
              })
              .catch((err) => {
                console.log(err);
              });
          }}
        >
          <>
            <MdRemoveCircle
              size={"2em"}
              style={{ color: "red", outline: "none" }}
            />
            <span
              className="p-2 font-weight-bold d-flex d-inline-flex"
              style={textColor !== null ? textColor : { color: "white" }}
            >
              Remove Exercise
            </span>
          </>
        </button>
        <br />
      </div>
    </>
  );
};
export default RemoveWorkout;
