import React, { useState, useEffect, createRef } from "react";
import axios from "axios";
import { Accordion, Card } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";
import SetNewWorkout from "./exercises/actions/create/SetNewWorkout";
import ViewWorkoutList from "./exercises/ViewWorkoutList";
import NewLogList from "./actions/create/NewLogList";
import RemoveLogList from "./actions/remove/RemoveLogList";
import { isMobile } from "react-device-detect";
import Swipe from "react-easy-swipe";
import update from "immutability-helper";
import EditLogList from "./actions/edit/EditLogList";
import ToggleRemoval from "../partials/ToggleRemoval";
import ToggleEdit from "../partials/ToggleEdit";
import { resolveToLocation } from "react-router-dom/modules/utils/locationUtils";
let qs = require("querystring");
const ViewLogList = () => {
  const { user, isAuthenticated } = useAuth0();
  const [isShown, SetShown] = useState([]);
  const [isEdit, SetEdit] = useState([]);
  const [isDisplayList, SetDisplayList] = useState([]);
  const [isCurrID, SetCurrID] = useState([]);
  const [isDeleteList, SetDeleteList] = useState([]);
  const [isDelete, SetDelete] = useState(false);
  const [isSwipedRight, SetOnSwipeRight] = useState(false);
  useEffect(() => {
    if (isAuthenticated) {
      axios
        .post("api/users", { username: user.email })
        .then((res) => {
          console.log(res);
          if (res.data !== 0) {
            fetchData(res.data);
          }
        })
        .catch((err) => console.log(err));
    }
  }, []);

  const fetchData = (log = null, update = false, newlog = false) => {
    console.log("fetchdata", log);

    if (!update) {
      Object.entries(log).forEach((id, index) => {
        console.log("element", id[1]);
        let logDesc = [];
        Object.entries(id[1]).forEach((el, idx) => {
          console.log(el[1]);
          if (el[0] === "_id") SetCurrID((isCurrID) => [...isCurrID, el[1]]);
          else if (el[0] !== "__v") {
            logDesc.push(el);
          }
        });
        SetDisplayList((isDisplayList) => [...isDisplayList, logDesc]);
        SetShown((isShown) => [...isShown, false]);
        SetEdit((isEdit) => [...isEdit, false]);
        SetDeleteList((isDeleteList) => [...isDeleteList, createRef()]);
      });
    } else {
      if (!newlog) {
        console.log("Updating log");
        let LogDesc = [];
        Object.entries(log).forEach((el, idx) => {
          console.log(el);
          LogDesc.push(el);
        });
        let index = isCurrID.indexOf(log._id);
        SetDisplayList((isDisplayList) => [
          ...isDisplayList.slice(0, index),
          LogDesc,
          ...isDisplayList.slice(index + 1),
        ]);
      } else {
        // axios.get(`/api/logs/${log}`).then((res) => {
        //   console.log(res);
        let LogDesc = [];
        Object.entries(log).forEach((el, idx) => {
          console.log(el);
          LogDesc.push(el);
        });
        SetDisplayList((isDisplayList) => [...isDisplayList, LogDesc]);
        SetShown((isShown) => [...isShown, false]);
        SetEdit((isEdit) => [...isEdit, false]);
        SetDeleteList((isDeleteList) => [...isDeleteList, createRef()]);
        // });
      }
      // } else {
      //   axios
      //     .get(`api/logs`, {
      //       params: {
      //         username: user.email,
      //       },
      //     })
      //     .then((res) => {
      //       console.log(res);
      //     })
      //     .catch((err) => console.log(err));
      // }
    }
  };

  const onDelete = () => {
    SetDelete(!isDelete);
  };

  const onSwipeRight = (pos, event) => {
    if (isDelete) {
      console.log(`Moved to the right`, event);
      SetOnSwipeRight(true);
    }
  };
  const onSwipeEnd = (event) => {
    if (isSwipedRight) {
      handleDelete(Number(event.target.id.replace(/log-accordion-/g, "")));
    }
  };

  const handleDelete = (index) => {
    isDeleteList[index].current.classList.add(
      "animate__animated",
      "animate__fadeOutRight"
    );
    isDeleteList[index].current.addEventListener("animationend", () => {
      axios
        .delete(`api/logs/${isCurrID[index]}`)
        .then((res) => {
          console.log(res.data);
        })
        .catch((err) => {
          console.log(err);
        });
      SetCurrID((isCurrID) => [
        ...isCurrID.slice(0, index),
        null,
        ...isCurrID.slice(index + 1),
      ]);
      SetDisplayList((isDisplayList) => [
        ...isDisplayList.slice(0, index),
        null,
        ...isDisplayList.slice(index + 1),
      ]);
      SetDeleteList((isDeleteList) => [
        ...isDeleteList.slice(0, index),
        null,
        ...isDeleteList.slice(index + 1),
      ]);
    });
    SetOnSwipeRight(false);
  };

  return (
    <>
      <div>
        {isDisplayList.map((el, index) => {
          if (el === null) {
            return <></>;
          }
          return (
            <Swipe
              onSwipeRight={onSwipeRight}
              onSwipeEnd={onSwipeEnd}
              allowMouseEvents={true}
              key={"log-disp" + index}
            >
              <Accordion className={"p-2"} ref={isDeleteList[index]}>
                <Card>
                  {
                    <>
                      <>
                        <RemoveLogList
                          isDelete={isDelete}
                          index={index}
                          deleteData={handleDelete}
                        />
                      </>
                      {el.map((log) => {
                        if (log[0] === "title") {
                          return (
                            <>
                              <Accordion.Toggle
                                as={Card.Header}
                                eventKey="0"
                                id={"log-accordion-" + `${index}`}
                                style={{
                                  transition: "1s",
                                  paddingLeft: !isDelete
                                    ? "1vw"
                                    : isMobile
                                    ? "13vw"
                                    : "6vw",
                                }}
                              >
                                {isEdit[index] ? (
                                  <div className={"ml-2 form-group"}>
                                    <label
                                      for={`log-title-${index}`}
                                      className="bmd-label-floating"
                                    >
                                      New Title:{" "}
                                    </label>
                                    <input
                                      id={`log-title-${index}`}
                                      className={"ml-2 form-control"}
                                      size={10}
                                      placeholder={log[1]}
                                    />
                                  </div>
                                ) : (
                                  <div
                                    className={isMobile ? "p-2" : "ml-3 d-flex"}
                                  >
                                    <h5 id={`log-title-${index}`}>{log[1]}</h5>
                                  </div>
                                )}
                              </Accordion.Toggle>
                            </>
                          );
                        } else if (log[0] === "description") {
                          return (
                            <Accordion.Collapse
                              eventKey={!isDelete ? "0" : "-1"}
                            >
                              <Card.Body>
                                <div className={"p-2"}>
                                  {isEdit[index] ? (
                                    <div className={"form-group"}>
                                      <label
                                        for={`log-desc-${index}`}
                                        className="bmd-label-floating"
                                      >
                                        Log Description:{" "}
                                      </label>
                                      <hr />
                                      <textarea
                                        id={`log-desc-${index}`}
                                        cols={20}
                                        className={"form-control"}
                                        rows={2}
                                        placeholder={
                                          log[0] === "description" && log[1]
                                        }
                                      />
                                    </div>
                                  ) : (
                                    <p id={`log-desc-${index}`}>
                                      {log[0] === "description" && log[1]}
                                    </p>
                                  )}
                                </div>
                                <hr />
                                <>
                                  {isEdit[index] ? (
                                    <EditLogList
                                      el={el}
                                      index={index}
                                      isCurrID={isCurrID}
                                      isEdit={isEdit}
                                      SetEdit={SetEdit}
                                      updateData={fetchData}
                                    />
                                  ) : (
                                    <ViewWorkoutList
                                      el={el}
                                      isCurrID={isCurrID}
                                      updateData={fetchData}
                                      logIndex={index}
                                      user={user}
                                    />
                                  )}
                                </>
                                <SetNewWorkout
                                  index={index}
                                  isShown={isShown}
                                  SetShown={SetShown}
                                  isCurrID={isCurrID}
                                  updateData={fetchData}
                                  textColor={{ color: "black" }}
                                />
                                <ToggleEdit
                                  isEdit={isEdit}
                                  SetEdit={SetEdit}
                                  index={index}
                                  target={"Log"}
                                  multiple={true}
                                />
                              </Card.Body>
                            </Accordion.Collapse>
                          );
                        }
                      })}
                    </>
                  }
                </Card>

                <Card>
                  {el.map((log) => {
                    if (log[0] === "date") {
                      let date = new Date(log[1]);
                      return date.toDateString();
                    }
                  })}
                </Card>
              </Accordion>
            </Swipe>
          );
        })}
      </div>
      <div className={"d-inline-start"}>
        <NewLogList updateData={fetchData} />
        <ToggleRemoval isDelete={isDelete} onDelete={onDelete} target={"Log"} />
      </div>
    </>
  );
};
export default ViewLogList;
