import React from "react";
import { MdRemoveCircle } from "react-icons/md";
import axios from "axios";
import update from "immutability-helper";

const RemoveWorkout = ({
  exercise,
  logIndex,
  isCurrID,
  updateData,
  textColor = null,
  user,
}) => {
  return (
    <>
      <div className={"d-flex d-inline-flex"}>
        <button
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
            borderColor: "none",
          }}
          onClick={(e) => {
            e.preventDefault();
            console.log("deleting", exercise._id);
            console.log("at", isCurrID[logIndex]);
            // SetEditList((isEditList) =>
            //   update(isEditList, {
            //     0: {
            //       val: {
            //         $splice: [[index, 1]],
            //       },
            //     },
            //   })
            // );
            // console.log(isEditList[0]);
            //edit the log
            // axios
            //   .patch(`api/logs/update/${isCurrID[logIndex]}`, {
            //     data: isEditList,
            //     date: date,
            //   })
            //   .then((response) => {
            //     console.log("Log Updated");
            //     updateData(false, isCurrID[logIndex]);
            //   })
            //   .catch((err) => {
            //     console.log(err);
            //   });

            axios
              .post(`api/logs/delete/${isCurrID[logIndex]}`, {
                exerciseID: exercise._id,
              })
              .then((response) => {
                console.log("RES", response);
                updateData(response.data, true, false);
              })
              .catch((err) => {
                console.log("ERROR:", err);
              });

            // console.log(response);
            // updateData(true, isCurrID[logIndex]);
          }}
        >
          <>
            <MdRemoveCircle
              size={"2em"}
              style={{ color: "red", outline: "none" }}
            />
            <span
              className="p-2 font-weight-bold d-flex d-inline-flex"
              style={textColor !== null ? textColor : { color: "white" }}
            >
              Remove Exercise
            </span>
          </>
        </button>
        <br />
      </div>
    </>
  );
};
export default RemoveWorkout;
