import React, { useState } from "react";
import { BrowserView, MobileView } from "react-device-detect";
import EditableTable from "../../../../partials/EditableTable";
import SubmitWorkout from "./SubmitWorkout";
import { Modal } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";
import { FcPlus } from "react-icons/fc";
import axios from "axios";

const SetNewWorkout = ({ index, isShown, SetShown, isCurrID, updateData }) => {
  const { user } = useAuth0();
  const [isWorkoutDesc, SetWorkoutDesc] = useState([]);

  const submitNewWorkout = (e, index) => {
    let logId = index;
    let logTitle = document.getElementById(`log-title-${logId}`).innerText;
    let exerciseName = document.querySelector(`#new-workout-name-${logId}`)
      .value;
    let exerciseDesc = document.querySelector(`#new-workout-desc-${logId}`)
      .value;
    if (exerciseName === null || exerciseName === "") {
      alert("Please provide the name");
      return;
    }
    let date = new Date();
    axios
      .post(`api/logs/add/${isCurrID[logId]}`, {
        username: user.email,
        title: logTitle,
        name: exerciseName,
        description: exerciseDesc,
        set: isWorkoutDesc[logId].set,
        reps: isWorkoutDesc[logId].reps,
        weight: isWorkoutDesc[logId].weight,
        date: date,
      })
      .then((response) => {
        console.log(response);
        updateData(true, isCurrID[logId]);
        toggleNewWorkout(e, index);
      })
      .catch((err) => {
        console.log(err);
      });
  };
  const SetExerciseDescription = (state, idx) => {
    SetWorkoutDesc((isWorkoutDesc) => [
      ...isWorkoutDesc.slice(0, idx),
      state,
      ...isWorkoutDesc.slice(idx + 1),
    ]);
  };
  const toggleNewWorkout = (e, index) => {
    e.preventDefault();
    SetShown((isShown) => [
      ...isShown.slice(0, index),
      !isShown[index],
      ...isShown.slice(index + 1),
    ]);
  };
  return (
    <>
      {isShown[index] && (
        <>
          <BrowserView>
            <div className={"p-2 ml-2 d-flex d-inline-flex"}>
              Name: <input size={25} id={`new-workout-name-${index}`} />
            </div>
            <div className={"p-2 ml-2 d-flex d-inline-flex"}>
              Description:{" "}
              <textarea rows={5} cols={50} id={`new-workout-desc-${index}`} />
            </div>
            <div className={"container"} style={{ overflowX: "auto" }}>
              <EditableTable
                index={index}
                thead={["Set", "Reps", "Weight"]}
                SetData={SetExerciseDescription}
                style={{
                  color: "black",
                  width: "100%",
                }}
                dataAvailable={[[1], [5], [5]]}
              />
              <div
                className={"d-flex d-inline-flex"}
                style={{ float: "right" }}
              >
                <SubmitWorkout
                  submitNewWorkout={submitNewWorkout}
                  cancelNewWorkout={toggleNewWorkout}
                  index={index}
                />
              </div>
            </div>
          </BrowserView>
          <MobileView>
            <div className={"container"} style={{ overflowX: "scroll" }}>
              <Modal show={isShown[index]}>
                <Modal.Header closeButton>
                  <Modal.Title>Add new workout</Modal.Title>
                </Modal.Header>
                <Modal.Body className={"container"}>
                  <div className={"p-2 ml-1 d-flex"}>
                    Name: <input size={25} id={`new-workout-name-${index}`} />
                  </div>
                  <div className={"p-2 ml-1 d-flex"}>
                    Description:{" "}
                    <textarea
                      rows={5}
                      cols={20}
                      id={`new-workout-desc-${index}`}
                    />
                  </div>
                  <EditableTable
                    index={index}
                    thead={["Set", "Reps", "Weight"]}
                    SetData={SetExerciseDescription}
                    style={{
                      color: "black",
                      width: "inherit",
                      fontSize: "1.5vh",
                      margin: "0",
                    }}
                  />
                </Modal.Body>
                <Modal.Footer>
                  <SubmitWorkout
                    submitNewWorkout={submitNewWorkout}
                    cancelNewWorkout={toggleNewWorkout}
                    index={index}
                  />
                </Modal.Footer>
              </Modal>
            </div>
          </MobileView>
        </>
      )}
      <div className={"d-flex d-inline-flex"} style={{ textAlign: "center" }}>
        <button
          id={index}
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            SetShown((isShown) => [
              ...isShown.slice(0, index),
              !isShown[index],
              ...isShown.slice(index + 1),
            ]);
          }}
        >
          <FcPlus size={"2em"} />
          <span className="p-2 font-weight-bold" style={{ color: "black" }}>
            Add New Exercise
          </span>
        </button>
        <br />
      </div>
    </>
  );
};
export default SetNewWorkout;
