import React from "react";
import { isMobile } from "react-device-detect";
import update from "immutability-helper";
const EditWorkout = ({ isEditList, SetEditList, ex, li }) => {
  return (
    <>
      <div className={"form-group"}>
        <label for={`exercise-input-${li}`} className={"bmd-label-floating"}>
          Name:{" "}
        </label>
        <input
          id={`exercise-input-${li}`}
          className={"form-control"}
          type="text"
          size={isMobile ? 20 : 30}
          placeholder={ex.name}
          onChange={(e) => {
            const index = li;
            const val = e.target.value;
            SetEditList((isEditList) =>
              update(isEditList, {
                0: {
                  val: {
                    [index]: { name: { $set: val } },
                  },
                },
              })
            );
          }}
        />
      </div>
      <hr />
      <div className="form-group">
        <label for={`exercise-desc-${li}`} className={"bmd-label-floating"}>
          Description:
        </label>
        <br />
        <textarea
          id={`exercise-desc-${li}`}
          className={"form-control"}
          placeholder={ex.description}
          cols={isMobile ? 25 : 50}
          rows={10}
          onChange={(e) => {
            const index = li;
            let val = e.target.value;
            SetEditList((isEditList) =>
              update(isEditList, {
                0: {
                  val: {
                    [index]: {
                      description: { $set: val },
                    },
                  },
                },
              })
            );
          }}
        />
      </div>
    </>
  );
};
export default EditWorkout;
