import React, { useState } from "react";
import { Button, Table, ListGroup } from "react-bootstrap";
import Delete from "@material-ui/icons/Delete";
import { wobble } from "react-animations";
import styled, { keyframes } from "styled-components";
import WarningModal from "./WarningModal";
const CustomTable = ({
  thead,
  data,
  SetDeleteList,
  SetRemoval = [],
  style,
  warning = false,
}) => {
  const Wobble = styled.div`
    animation: 1s ${keyframes`${wobble}`} infinite;
  `;
  const [isSelected, SetSelect] = useState(false);
  const [isSelectList, SetSelectList] = useState([]);
  const [isWarning, SetWarning] = useState(false);
  const toggleAll = () => {
    let parent = document.getElementById("checkbox-parent");
    let children = document.getElementsByName("checkbox-children");
    children.forEach((el) => {
      el.checked = parent.checked;
      if (parent.checked) {
        SetSelect(true);
        el.parentElement.parentElement.setAttribute(
          "style",
          "text-decoration: line-through;"
        );
        SetSelectList((isSelectList) => [
          ...isSelectList,
          el.parentElement.parentElement,
        ]);
      } else {
        SetSelect(false);
        el.parentElement.parentElement.setAttribute(
          "style",
          "text-decoration: none;"
        );
        SetSelectList([]);
      }
    });
  };
  const showRemoveIcon = (e) => {
    let children = document.getElementsByName("checkbox-children");
    let selected = 0;
    children.forEach((el) => {
      if (el.checked) selected += 1;
    });
    if (selected > 0) SetSelect(true);
    else SetSelect(false);
    document.getElementById("checkbox-parent").checked =
      selected === children.length;

    let rowEl = e.target.parentElement.parentElement;
    if (e.target.checked === true) {
      rowEl.setAttribute("style", "text-decoration: line-through;");
      SetSelectList((isSelectList) => [...isSelectList, rowEl]);
    } else {
      rowEl.setAttribute("style", "text-decoration: none");
      let array = [...isSelectList]; // make a separate copy of the array
      let index = array.indexOf(rowEl);
      if (index !== -1) {
        array = [...array.slice(0, index), ...array.slice(index + 1)];
        SetSelectList(array);
      }
    }
  };
  const getSelectedForRemoval = () => {
    let deleteList = [];
    isSelectList.map((el) => {
      data.forEach((item, idx) => {
        if (item[0].props !== undefined) {
          if (el.children.item(1).innerText === item[0].props.children) {
            deleteList.push(idx);
          }
        } else {
          if (el.children.item(1).innerText.trim() === item[0].trim()) {
            deleteList.push(idx);
          }
        }
      });
    });
    SetDeleteList([...deleteList]);
    SetWarning(!isWarning);
  };

  let theadArr = [];
  for (let key in thead) {
    if (thead.hasOwnProperty(key)) {
      theadArr.push(<th key={key}>{thead[key]}</th>);
    } else {
      theadArr.push(<th key={key}>{key}</th>);
    }
  }
  return (
    <>
      <Table
        striped
        bordered
        style={style !== undefined ? style : { color: "white" }}
      >
        <thead className="thead-dark">
          <tr id="table-thead-tr">
            <th>
              <input type="checkbox" id="checkbox-parent" onClick={toggleAll} />
            </th>
            {theadArr}
          </tr>
        </thead>
        <tbody>
          {Array.isArray(data) ? (
            data.map((el, idx) => {
              if (Array.isArray(el)) {
                return (
                  <tr key={"custom-tr" + el + idx}>
                    <td>
                      <input
                        type="checkbox"
                        defaultChecked={false}
                        name="checkbox-children"
                        onClick={(e) => {
                          showRemoveIcon(e);
                        }}
                      />
                    </td>
                    <>
                      {el.map((params, idx) => {
                        if (params.hasOwnProperty("html"))
                          return (
                            <td id={"html-content"}>
                              <ListGroup>
                                {params.html.map((li) => {
                                  return li;
                                })}
                              </ListGroup>
                            </td>
                          );
                        else return <td key={params}>{params}</td>;
                      })}
                    </>
                  </tr>
                );
              } else {
                return (
                  <tr key={"custom-tr" + el + idx}>
                    <td>
                      <input
                        type="checkbox"
                        defaultChecked={false}
                        name="checkbox-children"
                        onClick={(e) => {
                          showRemoveIcon(e);
                        }}
                      />
                    </td>
                    <td>{el}</td>
                  </tr>
                );
              }
            })
          ) : (
            <>
              <tr key={data}>
                <td>
                  <input
                    type="checkbox"
                    defaultChecked={false}
                    name="checkbox-children"
                    onClick={(e) => {
                      showRemoveIcon(e);
                    }}
                  />
                </td>
                <td>{data}</td>
              </tr>
            </>
          )}
        </tbody>
      </Table>

      {isSelected && (
        <div className="d-flex">
          <Wobble>
            <Button
              variant="outline-light"
              style={{ borderRadius: "10vw" }}
              onClick={getSelectedForRemoval}
            >
              <Delete color="secondary" />
            </Button>
          </Wobble>
        </div>
      )}
      <>
        {warning && (
          <WarningModal
            SetWarning={SetWarning}
            isWarning={isWarning}
            SetRemoval={SetRemoval}
            SetDeleteList={SetDeleteList}
          />
        )}
      </>
    </>
  );
};
export default CustomTable;
