import React, { useState } from "react";
import { BrowserView, MobileView } from "react-device-detect";
import EditableTable from "../partials/EditableTable";
import SubmitWorkout from "./SubmitWorkout";
import { Card, Modal } from "react-bootstrap";
import { useAuth0 } from "@auth0/auth0-react";
import { FcPlus } from "react-icons/fc";
import axios from "axios";

const SetNewWorkout = ({ index, isShown, SetShown, isCurrID }) => {
  const { user } = useAuth0();
  const [isWorkoutDesc, SetWorkoutDesc] = useState([]);
  const [isNewExercise, SetNewExercise] = useState([]);

  const submitNewWorkout = (e) => {
    let logTitle = document.getElementById("log-title-" + `${e.target.id}`)
      .innerText;
    let exerciseName = document.querySelector(
      "#new-workout-name" + `${e.target.id}`
    ).value;
    let logDesc = document.getElementById("log-desc-" + `${e.target.id}`)
      .innerText;
    if (exerciseName === null || exerciseName === "") {
      alert("Please provide the name");
      return;
    }
    let date = new Date();
    console.log(
      "log title:",
      logTitle,
      "exercise: ",
      exerciseName,
      "logDesc",
      logDesc,
      isWorkoutDesc
    );
    axios
      .post(`api/logs/add/${isCurrID[e.target.id]}`, {
        username: user.email,
        title: logTitle,
        name: exerciseName,
        description: logDesc,
        set: isWorkoutDesc[e.target.id].set,
        reps: isWorkoutDesc[e.target.id].reps,
        weight: isWorkoutDesc[e.target.id].weight,
        date: date,
      })
      .then((response) => {
        console.log(response);
      })
      .catch((err) => {
        console.log(err);
      });
    window.location.reload();
  };
  const SetExerciseDescription = (state, idx) => {
    SetWorkoutDesc((isWorkoutDesc) => [
      ...isWorkoutDesc.slice(0, idx),
      state,
      ...isWorkoutDesc.slice(idx + 1),
    ]);

    console.log(isWorkoutDesc[idx]);
  };
  const cancelNewWorkout = (e, index) => {
    e.preventDefault();
    SetNewExercise((isNewExercise) => [
      ...isNewExercise.slice(0, index),
      ...isNewExercise.slice(index + 1),
    ]);
    SetShown((isShown) => [
      ...isShown.slice(0, index),
      false,
      ...isShown.slice(index + 1),
    ]);
  };
  return (
    <>
      {isShown[index] && (
        <>
          <BrowserView>
            <div className={"p-2 ml-2 d-flex d-inline-flex"}>
              <input size={25} id={"new-workout-name" + `${index}`} />
            </div>
            <div className={"container"} style={{ overflowX: "auto" }}>
              <EditableTable
                index={index}
                thead={["Set", "Reps", "Weight"]}
                SetData={SetExerciseDescription}
                style={{
                  color: "black",
                  width: "100%",
                }}
              />
              <div
                className={"d-flex d-inline-flex"}
                style={{ float: "right" }}
              >
                <SubmitWorkout
                  submitNewWorkout={submitNewWorkout}
                  cancelNewWorkout={cancelNewWorkout}
                  index={index}
                />
              </div>
            </div>
          </BrowserView>
          <MobileView>
            <div className={"container"} style={{ overflowX: "auto" }}>
              <Modal show={isShown[index]}>
                <Modal.Header closeButton>
                  <Modal.Title>Add new workout</Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <div className={"p-2 ml-2 d-flex d-inline-flex"}>
                    <input size={25} id={"new-workout-name" + `${index}`} />
                  </div>
                  <EditableTable
                    index={index}
                    thead={["Set", "Reps", "Weight"]}
                    SetData={SetExerciseDescription}
                    style={{
                      color: "black",
                      width: "inherit",
                      fontSize: "1.7vh",
                      margin: "0",
                    }}
                  />
                </Modal.Body>
                <Modal.Footer>
                  <SubmitWorkout
                    submitNewWorkout={submitNewWorkout}
                    cancelNewWorkout={cancelNewWorkout}
                    index={index}
                  />
                </Modal.Footer>
              </Modal>
            </div>
          </MobileView>
        </>
      )}
      <div className={"d-flex d-inline-flex"} style={{ textAlign: "center" }}>
        <button
          id={index}
          style={{
            borderRadius: "10vw",
            background: "none",
            borderStyle: "hidden",
          }}
          onClick={(e) => {
            e.preventDefault();
            SetShown((isShown) => [
              ...isShown.slice(0, index),
              true,
              ...isShown.slice(index + 1),
            ]);
          }}
        >
          <FcPlus size={"2em"} />
        </button>
        <br />
        <span className="p-2 font-weight-bold" style={{ color: "black" }}>
          Add New Exercise
        </span>
      </div>
    </>
  );
};
export default SetNewWorkout;
