import React, { useState } from "react";
import { Button } from "react-bootstrap";
import axios from "axios";
import { useAuth0 } from "@auth0/auth0-react";
import { MdDone } from "react-icons/md";
const SubmitUpdate = ({
  index,
  workoutIndex = -1,
  isEdit,
  isEditList,
  isCurrID,
  updateData,
  SetEdit,
  target,
}) => {
  const { user } = useAuth0();
  const [isHover, SetHover] = useState(false);
  return (
    <>
      <Button
        variant={"outline-success"}
        style={{
          borderRadius: "10vw",
          borderStyle: "hidden",
        }}
        onClick={(e) => {
          e.preventDefault();
          let date = new Date();
          let logTitle = document.querySelector(`#log-title-${index}`);
          let logDesc = document.querySelector(`#log-desc-${index}`);
          if (target === "Log") {
            logTitle =
              logTitle.value === null || logTitle.value.length === 0
                ? logTitle.getAttribute("placeholder")
                : logTitle.value;
            logDesc =
              logDesc.value === null || logDesc.value.length === 0
                ? logDesc.getAttribute("placeholder")
                : logDesc.value;
          } else {
            logTitle = logTitle.innerText;
            logDesc = logDesc.innerText;
          }
          //edit the log
          axios
            .patch(`api/logs/update/${isCurrID[index]}`, {
              username: user.email,
              title: logTitle,
              description: logDesc,
              exercises: isEditList[0].val,
              date: date,
            })
            .then((response) => {
              console.log("Log Updated");
              if (workoutIndex !== -1) {
                SetEdit((isEdit) => [
                  ...isEdit.slice(0, workoutIndex),
                  !isEdit[workoutIndex],
                  ...isEdit.slice(workoutIndex + 1),
                ]);
              } else {
                SetEdit((isEdit) => [
                  ...isEdit.slice(0, index),
                  !isEdit[index],
                  ...isEdit.slice(index + 1),
                ]);
              }
              updateData(true, isCurrID[index]);
            })
            .catch((err) => {
              console.log(err);
            });
        }}
      >
        <MdDone size={"2vh"} style={{ color: "green" }} />
        <span
          className="p-2 font-weight-bold"
          onMouseEnter={() => {
            SetHover(!isHover);
          }}
          onMouseOut={() => {
            SetHover(!isHover);
          }}
          style={{ color: isHover ? "white" : "black" }}
        >
          Save?
        </span>
      </Button>
      <Button
        className={"p-2"}
        variant={"outline-danger"}
        style={{
          borderRadius: "10vw",
          borderStyle: "hidden",
        }}
        onClick={(e) => {
          if (workoutIndex !== -1) {
            SetEdit((isEdit) => [
              ...isEdit.slice(0, workoutIndex),
              !isEdit[workoutIndex],
              ...isEdit.slice(workoutIndex + 1),
            ]);
          } else {
            SetEdit((isEdit) => [
              ...isEdit.slice(0, index),
              !isEdit[index],
              ...isEdit.slice(index + 1),
            ]);
          }
        }}
      >
        Cancel
      </Button>
      <hr />
    </>
  );
};
export default SubmitUpdate;
